import { getBase64ImageDetails } from "../getBase64ImageDetails";

const exampleBase64SVGImage = ``;
const exampleBase64PNGImage = ``;

describe("getBase64ImageDetails", () => {
  it("should return true for a valid base64-encoded SVG", () => {
    expect(getBase64ImageDetails(exampleBase64SVGImage)).toStrictEqual({
      type: "svg+xml",
      base64Prefix: "data:image/svg+xml;base64,",
      isValidBase64Image: true,
    });
  });

  it("should return true for a valid base64-encoded PNG", () => {
    expect(getBase64ImageDetails(exampleBase64PNGImage)).toStrictEqual({
      type: "png",
      base64Prefix: "data:image/png;base64,",
      isValidBase64Image: true,
    });
  });

  it("should return false for a plain text string", () => {
    const plainText = "This is just a regular string.";

    expect(getBase64ImageDetails(plainText)).toStrictEqual({
      type: null,
      base64Prefix: null,
      isValidBase64Image: false,
    });
  });

  it("should return false for an invalid base64 string", () => {
    const invalidBase64 = "Not-a-base64-string===";

    expect(getBase64ImageDetails(invalidBase64)).toStrictEqual({
      type: null,
      base64Prefix: null,
      isValidBase64Image: false,
    });
  });

  it("should return false for an empty string", () => {
    expect(getBase64ImageDetails("")).toStrictEqual({
      type: null,
      base64Prefix: null,
      isValidBase64Image: false,
    });
  });

  it("should return false for a null value", () => {
    expect(getBase64ImageDetails(null as unknown as string)).toStrictEqual({
      type: null,
      base64Prefix: null,
      isValidBase64Image: false,
    });
  });

  it("should return false for a valid URL", () => {
    const url = "https://example.com/image.png";

    expect(getBase64ImageDetails(url)).toStrictEqual({
      type: null,
      base64Prefix: null,
      isValidBase64Image: false,
    });
  });

  it("should return false for a string with base64-like characters but not valid base64", () => {
    const invalidBase64Image = "data:image/png;base64,notvalidbase64string";

    expect(getBase64ImageDetails(invalidBase64Image)).toStrictEqual({
      type: null,
      base64Prefix: null,
      isValidBase64Image: false,
    });
  });
});
